import './App.css';
import React from 'react';

class App extends React.Component {
  state = {
    error: null,
    isLoaded: false,
    articles: []
  };
  /*
  constructor(props){
    super(props);
    this.mystate = {
      title: '', 
      description: '', 
      author: ''
    };
  }


  handleChange = (event) =>{
      this.setState({[event.target.name]: event.target.value});
  }

  handleSubmit = (event) => {
    fetch("http://localhost:8000/api/articles/",{
      method: 'POST',
      mode: 'cors',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(this.mystate),
    }).then((res) => {
      console.log(res)
      return res.json()
    });
    event.preventDefault();
    console.log(this.mystate);
  }
*/
  componentDidMount() {
    fetch("http://localhost:8000/api/articles/")
      .then(res => res.json())
      .then(
        (data) => {
          this.setState({
            isLoaded: true,
            articles: data
          });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  render() {
    const { error, isLoaded, articles } = this.state;

    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <div class="ap">
          <div id="article">
            <ul>
              {articles.map(article => (
                <li key={article.id}>
                  <h1>{article.title}</h1>
                  <p id="box">{article.description}</p>
                  <strong>Author: {article.author}</strong>
                  <p>Posted at: {article.created}</p>
                </li>
              ))}
            </ul>
          </div>
        </div>
      );
    }
  }
}

export default App;
