import './App.css';
import React, { Component } from 'react';
import axios from 'axios';

class ArticleList extends Component {
  state = {
    error: null,
    isLoaded: false,
    articles: []
  };

  constructor(props) {
    super(props)
    this.mystate = { isModalOpen: false }
  }

  openModal() {
    this.setState({ isModalOpen: true })
  }

  closeModal() {
    this.setState({ isModalOpen: false })
  }
  componentDidMount() {
    fetch("http://localhost:8000/api/articles/")
      .then(res => res.json())
      .then(
        (data) => {
          this.setState({
            isLoaded: true,
            articles: data
          });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }
  deleteArticle = (id,e)=> {
    axios.delete(`http://localhost:8000/api/articles/${id}/`)
    .then((res)=> {
      const articles = this.state.articles.filter(article => article.id !== id);  
      this.setState({ articles }); 
      return res.data; 
    })
    .catch(error =>{
      console.log(error.message);
    })

  }
  render() {
    const { error, isLoaded, articles } = this.state;

    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
          <div id="article">
            <ul>
              {articles.map(article => (
                <li key={article.id}>
                  <h1>{article.title}</h1>
                  <p id="box">{article.description}</p>
                  <strong>Author: {article.author}</strong>
                  <p>Posted at: {article.created} &nbsp;  <button onClick={(e) => this.openModal(article,e)}>Update</button>&nbsp; <button onClick={(e)=> this.deleteArticle(article.id, e)}>Detele</button></p>
                </li>
              ))}
            </ul>
            <div>
              <Modal isOpen={this.state.isModalOpen} onClose={() => this.closeModal()}>
                <h3>Modal title</h3>
                <p>Content</p>
              </Modal>
            </div>
          </div>
      );
    }
  }
}
class Modal extends React.Component {
  render() {
    if (this.props.isOpen === false)
      return null

    return (
      <div>
        <div className="modal">
          {this.props.children}
        </div>
        <div className="bg" onClick={e => this.close(e)}/>
      </div>
    )
  }

  close(e) {
    e.preventDefault()

    if (this.props.onClose) {
      this.props.onClose()
    }
  }
}

export default ArticleList;
