import './App.css';
import React, { Component } from 'react';
import axios from 'axios';

class ArticleList extends Component {
  state = {
    error: null,
    isLoaded: false,
    articles: []
  };

  componentDidMount() {
    fetch("http://localhost:8000/api/articles/")
      .then(res => res.json())
      .then(
        (data) => {
          this.setState({
            isLoaded: true,
            articles: data
          });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }
  deleteArticle = (id,e)=> {
    axios.delete(`http://localhost:8000/api/articles/${id}/`)
    .then((res)=>{
      if(res){
        alert("Article has been deteled!");
      }
      console.log(res);
      console.log(res.data);
      const articles = this.state.articles.filter(article => article.id !== id);  
      this.setState({ articles });  
    })
    .catch(error =>{
      console.log(error)
    })

  }
  render() {
    const { error, isLoaded, articles } = this.state;

    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
          <div id="article">
            <ul>
              {articles.map(article => (
                <li key={article.id}>
                  <h1>{article.title}</h1>
                  <p id="box">{article.description}</p>
                  <strong>Author: {article.author}</strong>
                  <p>Posted at: {article.created} <button onClick={(e)=> this.deleteArticle(article.id, e)}>Detele</button></p>
                </li>
              ))}
            </ul>
          </div>
      );
    }
  }
}

export default ArticleList;
