import './App.css';
import React from 'react';

class App extends React.Component {
  state = {
    error: null,
    isLoaded: false,
    articles: []
  };
  constructor(props){
    super(props);
    this.mystate = {myarticles:''};
  }

  handleChange = (event) =>{
    this.setState({[event.target.myarticles]: event.target.value});
  }

  handleSubmit = (event) => {
    alert('A form was submitted: '+ this.mystate);
    fetch("http://localhost:8000/api/articles/",{
      method: 'POST',
      body: JSON.stringify(this.mystate)
    }).then(res => {return res.json()});
    event.preventDefault();
  }

  componentDidMount() {
    fetch("http://localhost:8000/api/articles/")
      .then(res => res.json())
      .then(
        (data) => {
          this.setState({
            isLoaded: true,
            articles: data
          });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  render() {
    const { error, isLoaded, articles } = this.state;

    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <div class="ap">
          <div id="form">
              <h4>Create an Article</h4>
              <form onSubmit={this.handleSubmit} id="articleForm">
                <label>Title:</label>
                <input type="text" name="title"  onChange={this.handleChange}/><br></br>
                <label>Description:</label>
                <textarea name="description"  onChange={this.handleChange} form="articleForm" rows="4" cols="50">Write an article here...</textarea><br></br>
                <label>Author:</label>
                <input type="text" name="author"  onChange={this.handleChange}/><br></br>
                <input type="submit" value="Save"/>
              </form>
          </div>
          <div id="article">
            <ul>
              {articles.map(article => (
                <li key={article.id}>
                  <h1>{article.title}</h1>
                  <p id="box">{article.description}</p>
                  <strong>Author: {article.author}</strong>
                  <p>Posted at: {article.created}</p>
                </li>
              ))}
            </ul>
          </div>
        </div>
      );
    }
  }
}

export default App;
